import React, { useState } from "react";
import {
  Table,
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Paper,
  Button,
  Modal,
  TextField,
  IconButton,
  Box,
  InputLabel,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import CloseIcon from "@mui/icons-material/Close";

const Companies = () => {
  const [companies, setCompanies] = useState([]);
  const [openModal, setOpenModal] = useState(false);
  const [newCompany, setNewCompany] = useState({
    name: "",
    logo: null,
  });
  const [donatedLengths, setDonatedLengths] = useState({});
  const [logoPreview, setLogoPreview] = useState(null);

  const handleAddCompany = () => {
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
    setNewCompany({
      name: "",
      logo: null,
    });
    setLogoPreview(null);
  };

  const handleAddNewCompany = () => {
    setCompanies([...companies, newCompany]);
    handleCloseModal();
  };

  const handleAddDonatedLength = (companyId, length) => {
    setDonatedLengths({
      ...donatedLengths,
      [companyId]: (donatedLengths[companyId] || 0) + parseFloat(length),
    });
  };

  const handleLogoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setLogoPreview(reader.result);
      };
      reader.readAsDataURL(file);
      setNewCompany({ ...newCompany, logo: file });
    }
  };

  return (
    <Box
      maxWidth="70%"
      margin="2rem auto"
      paddingTop="4rem"
      position="relative"
    >
      <IconButton
        onClick={handleAddCompany}
        style={{ position: "absolute", top: 0, right: 0 }}
      >
        <AddIcon />
      </IconButton>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Company ID</TableCell>
              <TableCell>Company Name</TableCell>
              <TableCell>Company Logo</TableCell>
              <TableCell>Last Donated Hair Length</TableCell>
              <TableCell>Total Donated Hair Length</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {companies.map((company) => (
              <TableRow key={company.id}>
                <TableCell>{company.id}</TableCell>
                <TableCell>{company.name}</TableCell>
                <TableCell>
                  <img
                    src={company.logo}
                    alt={`${company.name} Logo`}
                    style={{ width: "50px", height: "auto" }}
                  />
                </TableCell>
                <TableCell>{donatedLengths[company.id] || 0}</TableCell>
                <TableCell>{donatedLengths[company.id] || 0}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <Modal open={openModal} onClose={handleCloseModal}>
        <div
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            backgroundColor: "#fff",
            borderRadius: "5px",
            maxWidth: "70%",
            padding: "20px",
          }}
        >
          <IconButton
            onClick={handleCloseModal}
            style={{ position: "absolute", top: "10px", right: "10px" }}
          >
            <CloseIcon />
          </IconButton>
          <InputLabel>Company Name</InputLabel>
          <TextField
            fullWidth
            margin="normal"
            value={newCompany.name}
            onChange={(e) =>
              setNewCompany({ ...newCompany, name: e.target.value })
            }
          />
          <InputLabel>Company Logo</InputLabel>
          <input
            type="file"
            accept="image/*"
            onChange={handleLogoChange}
            style={{ marginBottom: "1rem" }}
          />
          {logoPreview && (
            <img
              src={logoPreview}
              alt="Logo Preview"
              style={{ maxWidth: "100%", maxHeight: "200px", marginBottom: "1rem" }}
            />
          )}
          <Button
            variant="contained"
            color="primary"
            onClick={handleAddNewCompany}
          >
            Add Company
          </Button>
        </div>
      </Modal>
    </Box>
  );
};

export default Companies;
