import Request from "../helpers/RequestHelper";

const fetchData = async () => {
  const res = await Request("post", "/mainpage/");
  console.log(res);
  return res;
};

const createCompany = async (data) => {
  const res = await Request("post", "/admin/menu/addCompany/", data);
  return res;
};

const updateCompany = async (row) => {
  const res = await Request(
    "post",
    "/admin/menu/updateCompany/" + row.id + "/",
    {
      new_donation: row.donatedHair,
    }
  );
  return res;
};
const deleteCompany = async (row) => {
  const res = await Request(
    "post",
    "/admin/deleteCompany/" + row.id + "/"
  );
  return res;
};

const companyService = {
  fetchData,
  createCompany,
  updateCompany,
};

export default companyService;
