import React, { useState } from "react";
import {
  Table,
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Paper,
  Button,
  Modal,
  TextField,
  IconButton,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import CloseIcon from "@mui/icons-material/Close";

const Companies = () => {
  const [companies, setCompanies] = useState([]);
  const [openModal, setOpenModal] = useState(false);
  const [newCompany, setNewCompany] = useState({
    name: "",
    logo: null,
  });
  const [donatedLengths, setDonatedLengths] = useState({});

  const handleAddCompany = () => {
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
    setNewCompany({
      name: "",
      logo: null,
    });
  };

  const handleAddNewCompany = () => {
    // Add the new company to the list
    setCompanies([...companies, newCompany]);
    // Close the modal
    handleCloseModal();
  };

  const handleAddDonatedLength = (companyId, length) => {
    setDonatedLengths({
      ...donatedLengths,
      [companyId]: (donatedLengths[companyId] || 0) + parseFloat(length),
    });
  };

  return (
    <div style={{ maxWidth: "70%", margin: "0 auto", paddingTop: "80px" }}>
      <IconButton
        onClick={handleAddCompany}
        style={{ position: "absolute", top: "0", right: "0", zIndex: "1" }}
      >
        <AddIcon />
      </IconButton>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Company ID</TableCell>
              <TableCell>Company Name</TableCell>
              <TableCell>Company Logo</TableCell>
              <TableCell>Last Donated Hair Length</TableCell>
              <TableCell>Total Donated Hair Length</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {companies.map((company) => (
              <TableRow key={company.id}>
                <TableCell>{company.id}</TableCell>
                <TableCell>{company.name}</TableCell>
                <TableCell>
                  <img
                    src={company.logo}
                    alt={`${company.name} Logo`}
                    style={{ width: "50px", height: "auto" }}
                  />
                </TableCell>
                <TableCell>{donatedLengths[company.id] || 0}</TableCell>
                <TableCell>
                  {donatedLengths[company.id] || 0}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Modal open={openModal} onClose={handleCloseModal}>
        <div style={{ padding: "20px", backgroundColor: "#fff", borderRadius: "5px" }}>
          <IconButton
            onClick={handleCloseModal}
            style={{ position: "absolute", top: "10px", right: "10px" }}
          >
            <CloseIcon />
          </IconButton>
          <TextField
            label="Company Name"
            fullWidth
            margin="normal"
            value={newCompany.name}
            onChange={(e) => setNewCompany({ ...newCompany, name: e.target.value })}
          />
          <TextField
            label="Company Logo"
            fullWidth
            margin="normal"
            type="file"
            onChange={(e) => setNewCompany({ ...newCompany, logo: e.target.files[0] })}
          />
          <Button
            variant="contained"
            color="primary"
            onClick={handleAddNewCompany}
          >
            Add Company
          </Button>
        </div>
      </Modal>
    </div>
  );
};

export default Companies;
