import Request from "../helpers/RequestHelper";

const API_URL = "companyController";

const fetchData = async () => {
  const res = await Request("post", API_URL + "/companies/");
  return res;
};

const createCompany = async (data) => {
  const res = await Request("post", API_URL + "/create/", data);
  return res;
};

const updateCompany = async (row) => {
  const res = await Request("post", API_URL + "/update/", {
    id: row.id,
    donatedHair: row.donatedHair,
  });
  return res;
};

const companyService = {
  fetchData,
  createCompany,
  updateCompany,
};

export default companyService;
