import React, { useState } from "react";
import {
  Table,
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Paper,
  Button,
  Modal,
  TextField,
  IconButton,
  Box,
  InputLabel
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import CloseIcon from "@mui/icons-material/Close";

const Companies = () => {
  const [companies, setCompanies] = useState([]);
  const [openModal, setOpenModal] = useState(false);
  const [newCompany, setNewCompany] = useState({
    name: "",
    logo: null,
  });
  const [donatedLengths, setDonatedLengths] = useState({});

  const handleAddCompany = () => {
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
    setNewCompany({
      name: "",
      logo: null,
    });
  };

  const handleAddNewCompany = () => {
    // Add the new company to the list
    setCompanies([...companies, newCompany]);
    // Close the modal
    handleCloseModal();
  };

  const handleAddDonatedLength = (companyId, length) => {
    setDonatedLengths({
      ...donatedLengths,
      [companyId]: (donatedLengths[companyId] || 0) + parseFloat(length),
    });
  };

  return (
    <Box
      maxWidth="70%"
      margin="2rem auto"
      paddingTop="4rem"
      position="relative"
    >
      <IconButton
        onClick={handleAddCompany}
        style={{ position: "absolute", top: 0, right: 0 }}
      >
        <AddIcon />
      </IconButton>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Company ID</TableCell>
              <TableCell>Company Name</TableCell>
              <TableCell>Company Logo</TableCell>
              <TableCell>Last Donated Hair Length</TableCell>
              <TableCell>Total Donated Hair Length</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {companies.map((company) => (
              <TableRow key={company.id}>
                <TableCell>{company.id}</TableCell>
                <TableCell>{company.name}</TableCell>
                <TableCell>
                  <img
                    src={company.logo}
                    alt={`${company.name} Logo`}
                    style={{ width: "50px", height: "auto" }}
                  />
                </TableCell>
                <TableCell>{donatedLengths[company.id] || 0}</TableCell>
                <TableCell>{donatedLengths[company.id] || 0}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <Modal open={openModal} onClose={handleCloseModal}>
        <div
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            backgroundColor: "#fff",
            borderRadius: "5px",
            maxWidth: "70%",
            padding: "20px",
          }}
        >
          <IconButton
            onClick={handleCloseModal}
            style={{ position: "absolute", top: "10px", right: "10px" }}
          >
            <CloseIcon />
          </IconButton>
          <InputLabel>Company Name</InputLabel>
          <TextField
            fullWidth
            margin="normal"
            value={newCompany.name}
            onChange={(e) =>
              setNewCompany({ ...newCompany, name: e.target.value })
            }
          />
          <InputLabel>Company Logo</InputLabel>
          <TextField
            fullWidth
            margin="normal"
            type="file"
            onChange={(e) =>
              setNewCompany({ ...newCompany, logo: e.target.files[0] })
            }
          />
          <Button
            variant="contained"
            color="primary"
            onClick={handleAddNewCompany}
          >
            Add Company
          </Button>
        </div>
      </Modal>
    </Box>
  );
};

export default Companies;
