import React, { useState } from "react";
import {
  Table,
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Paper,
  Button,
  Modal,
  TextField,
  IconButton,
  Box,
  InputLabel,
  styled,
  Tooltip,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import CloseIcon from "@mui/icons-material/Close";
import PencilIcon from "@mui/icons-material/Create";
import AcceptIcon from "@mui/icons-material/Check";
import RejectIcon from "@mui/icons-material/Close";

const StyledModalContent = styled("div")(({ theme }) => ({
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  backgroundColor: "#fff",
  borderRadius: "5px",
  maxWidth: "70%",
  width: "400px",
  padding: "20px",
  boxShadow: theme.shadows[5],
  outline: "none",
}));

const sampleCompanies = [
  {
    id: 1,
    name: "Company A",
    logo: "https://via.placeholder.com/50x50",
  },
  {
    id: 2,
    name: "Company B",
    logo: "https://via.placeholder.com/50x50",
  },
  {
    id: 3,
    name: "Company C",
    logo: "https://via.placeholder.com/50x50",
  },
  {
    id: 4,
    name: "Company D",
    logo: "https://via.placeholder.com/50x50",
  },
];

const sampleDonatedLengths = {
  1: 15,
  2: 20,
  3: 10,
  4: 5,
};

const Companies = () => {
  const [companies, setCompanies] = useState(sampleCompanies);
  const [openModal, setOpenModal] = useState(false);
  const [newCompany, setNewCompany] = useState({
    name: "",
    logo: null,
  });
  const [donatedLengths, setDonatedLengths] = useState(sampleDonatedLengths);
  const [logoPreview, setLogoPreview] = useState(null);
  const [donatedHairInput, setDonatedHairInput] = useState({});

  const handleAddDonatedHair = (companyId) => {
    // Handle showing the input field for donated hair
    setDonatedHairInput((prevState) => ({
      ...prevState,
      [companyId]: donatedLengths[companyId] || "",
    }));
  };

  const handleAcceptDonatedHair = (companyId) => {
    // Handle accepting donated hair and making backend request
    // Update donatedLengths state if successful
    // Reset donatedHairInput
    setDonatedHairInput((prevState) => ({
      ...prevState,
      [companyId]: "",
    }));
  };

  const handleRejectDonatedHair = (companyId) => {
    // Handle rejecting donated hair and reverting to pencil icon
    setDonatedHairInput((prevState) => ({
      ...prevState,
      [companyId]: "",
    }));
  };

  const handleDonatedHairInputChange = (companyId, value) => {
    setDonatedHairInput((prevState) => ({
      ...prevState,
      [companyId]: value,
    }));
  };

  return (
    <Box
      maxWidth="90%"
      margin="2rem auto"
      paddingTop="4rem"
      position="relative"
    >
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Company ID</TableCell>
              <TableCell>Company Name</TableCell>
              <TableCell>Company Logo</TableCell>
              <TableCell>Last Donated Hair Length</TableCell>
              <TableCell>Total Donated Hair Length</TableCell>
              <TableCell>Add Donated Hair</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {companies.map((company) => (
              <TableRow key={company.id}>
                <TableCell>{company.id}</TableCell>
                <TableCell>{company.name}</TableCell>
                <TableCell>
                  <img
                    src={company.logo}
                    alt={`${company.name} Logo`}
                    style={{ width: "50px", height: "auto" }}
                  />
                </TableCell>
                <TableCell>{donatedLengths[company.id] || 0}</TableCell>
                <TableCell>{donatedLengths[company.id] || 0}</TableCell>
                <TableCell>
                  {donatedHairInput[company.id] !== undefined ? (
                    <>
                      <TextField
                        type="number"
                        value={donatedHairInput[company.id]}
                        onChange={(e) =>
                          handleDonatedHairInputChange(
                            company.id,
                            e.target.value
                          )
                        }
                      />
                      <IconButton
                        onClick={() => handleAcceptDonatedHair(company.id)}
                        aria-label="Accept Donated Hair"
                      >
                        <AcceptIcon />
                      </IconButton>
                      <IconButton
                        onClick={() => handleRejectDonatedHair(company.id)}
                        aria-label="Reject Donated Hair"
                      >
                        <RejectIcon />
                      </IconButton>
                    </>
                  ) : (
                    <IconButton
                      onClick={() => handleAddDonatedHair(company.id)}
                      aria-label="Add Donated Hair"
                    >
                      <PencilIcon />
                    </IconButton>
                  )}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Modal open={openModal} onClose={handleCloseModal}>
        <StyledModalContent>
          <Tooltip title="Close Modal">
            <IconButton
              onClick={handleCloseModal}
              style={{ position: "absolute", top: "10px", right: "10px" }}
            >
              <CloseIcon />
            </IconButton>
          </Tooltip>

          <InputLabel style={{ marginTop: "2rem" }}>Company Name</InputLabel>
          <TextField
            fullWidth
            margin="normal"
            value={newCompany.name}
            onChange={(e) =>
              setNewCompany({ ...newCompany, name: e.target.value })
            }
          />
          <InputLabel style={{ marginTop: "1rem" }}>Company Logo</InputLabel>
          <input
            type="file"
            accept="image/*"
            onChange={handleLogoChange}
            style={{ margin: ".5rem auto" }}
          />
          {logoPreview && (
            <img
              src={logoPreview}
              alt="Logo Preview"
              style={{
                maxWidth: "100%",
                maxHeight: "200px",
                marginBottom: "1rem",
              }}
            />
          )}
          <div
            style={{
              display: "flex",
              justifyContent: "flex-end",
              marginTop: "2rem",
            }}
          >
            <Button
              variant="contained"
              color="primary"
              onClick={handleAddNewCompany}
              fullWidth
            >
              Add Company
            </Button>
          </div>
        </StyledModalContent>
      </Modal>
    </Box>
  );
};

export default Companies;
