  14: 5,
  15: 5,
  16: 5,
};

const Companies = () => {
  const [companies, setCompanies] = useState(sampleCompanies);
  const [openModal, setOpenModal] = useState(false);
  const [newCompany, setNewCompany] = useState({
    name: "",
    logo: null,
  });
  const [donatedLengths, setDonatedLengths] = useState(sampleDonatedLengths);
  const [logoPreview, setLogoPreview] = useState(null);
  const [donatedHairInput, setDonatedHairInput] = useState({});
  const [selectedCompanyId, setSelectedCompanyId] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 8; // Change this to adjust the number of items per page
  const [loading, setLoading] = useState(false);

  const handleAddCompany = () => {
    setOpenModal(true);
  };

  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const fileReader = new FileReader();
      fileReader.readAsDataURL(file);

      fileReader.onload = () => {
        resolve(fileReader.result);
      };

      fileReader.onerror = (error) => {
        reject(error);
      };
    });
  };

  const init = React.useCallback(async () => {
    setLoading(true);
    const res = companyService.fetchData();
    console.log(res);
    if (res?.status === 200) {
      setCompanies(res?.data);
    }
    setLoading(false);
  }, []);

  useEffect(() => {
    init();
  }, [init]);

  const handleCloseModal = () => {
    setOpenModal(false);
    setNewCompany({
      name: "",
      logo: null,
    });
    setLogoPreview(null);
  };

  const handleAddNewCompany = () => {
    setLoading(true);
    const newCompanyProps = {
      companyName: newCompany.name,
      companyLogo: convertToBase64(newCompany.logo),
    };
    const res = companyService.createCompany(newCompanyProps);
    console.log(res);
    if (res?.status === 200) {
      setCompanies(res?.data);
    }
    setLoading(false);
    handleCloseModal();
    window.location.reload();
  };

  const handleLogoChange = (event) => {
    const file = event.target.files[0];
    setNewCompany({
      ...newCompany,
      logo: file,
    });
    // Preview the logo image
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setLogoPreview(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleAddDonatedHair = (companyId) => {
    setSelectedCompanyId(companyId);
    // Handle showing the input field for donated hair
    setDonatedHairInput((prevState) => ({
      ...prevState,
      [companyId]: donatedLengths[companyId] || "",
    }));
  };
  const handleAcceptDonatedHair = async (companyId) => {
    setDonatedHairInput((prevState) => ({
      ...prevState,
      [companyId]: "",
    }));
    setLoading(true);
    const updateCompanyProps = {
      id: companyId,
      donatedHair: donatedHairInput[companyId],
    };
    try {
      const res = await companyService.updateCompany(updateCompanyProps);
      if (res?.status === 200) {
        setCompanies((prevState) =>
          prevState.map((company) =>
            company.id === companyId
              ? {
                  ...company,
                  lastDonatedHair: company.lastDonatedHair,
                  totalDonatedHair: company.totalDonatedHair,
                }
              : company
          )
        );
      }
    } catch (error) {
      console.error("Error updating company:", error);
    }
    setLoading(false);
    setSelectedCompanyId(null);
  };

  const handleRejectDonatedHair = (companyId) => {
    // Handle rejecting donated hair and reverting to pencil icon
    setDonatedHairInput((prevState) => ({
      ...prevState,
      [companyId]: "",
    }));
    setSelectedCompanyId(null);
  };

  const handleDonatedHairInputChange = (companyId, value) => {
    setDonatedHairInput((prevState) => ({
      ...prevState,
      [companyId]: value,
    }));
  };

  const handlePageChange = (event, newPage) => {
    setCurrentPage(newPage);
  };

  // Calculate the index range for items to display based on pagination
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;

  return (
    <Box
      maxWidth="90%" // Increase the maximum width for smaller screen sizes
      margin="2rem auto"
      paddingTop="4rem"
      position="relative"
    >
      <Tooltip title="Add Company">
        <IconButton
          onClick={handleAddCompany}
          sx={{
            position: "absolute",
            top: 0,
            right: 0,
            background: "linear-gradient(to right, #8000BA, #FF4B00)",
            boxShadow: "0px 3px 6px rgba(0, 0, 0, 0.1)",
            color: "white",
            transition:
              "transform 0.3s ease-in-out, background 0.3s ease-in-out",
            "&:hover": {
              background: "linear-gradient(to right, #FF4B00, #8000BA)",
              transform: "scale(1.05)",
            },
          }}
        >
          <AddIcon />
        </IconButton>
      </Tooltip>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Company ID</TableCell>
              <TableCell>Company Name</TableCell>
              <TableCell>Company Logo</TableCell>
              <TableCell>Last Donated Hair Length</TableCell>
              <TableCell>Total Donated Hair Length</TableCell>
              <TableCell>Add Donated Hair</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {companies.slice(startIndex, endIndex).map((company) => (
              <TableRow key={company.id}>
                <TableCell>{company.id}</TableCell>
                <TableCell>{company.name}</TableCell>
                <TableCell>
                  <img
                    src={company.logo}
                    alt={`${company.name} Logo`}
                    style={{ width: "50px", height: "auto" }}
                  />
                </TableCell>
                <TableCell>{donatedLengths[company.id] || 0}</TableCell>
                <TableCell>{donatedLengths[company.id] || 0}</TableCell>
                <TableCell>
                  {selectedCompanyId === company.id ? (
                    <div
                      style={{
                        display: "flex",
                        alignItems: "center",
                        fontSize: "14px", // Font boyutunu artırın
                      }}
                    >
                      <TextField
                        value={donatedHairInput[company.id] || ""}
                        onChange={(e) =>
                          handleDonatedHairInputChange(
                            company.id,
                            e.target.value
                          )
                        }
                        size="small"
                        type="number"
                        sx={{ mr: 1 }}
                        inputProps={{
                          style: {
                            fontSize: "14px",
                            padding: "5px",
                            width: "40px", // Input alanının genişliğini artırın
                          },
                        }}
                      />
                      <IconButton
                        onClick={() => handleAcceptDonatedHair(company.id)}
                        aria-label="Accept Donated Hair"
                        style={{ color: "green" }}
                      >
                        <AcceptIcon />
                      </IconButton>
                      <IconButton
                        onClick={() => handleRejectDonatedHair(company.id)}
                        aria-label="Reject Donated Hair"
                        style={{ color: "red" }}
                      >
                        <RejectIcon />
                      </IconButton>
                    </div>
                  ) : (
                    <IconButton
                      onClick={() => handleAddDonatedHair(company.id)}
                      aria-label="Add Donated Hair"
                      sx={{ padding: "8px" }} // İkonun iç boşluğunu artırın
                    >
                      <PencilIcon sx={{ fontSize: 18, color: "#8000BA" }} />
                      {/* İkon boyutunu artırın */}
                    </IconButton>
                  )}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <Box sx={{ display: "flex", justifyContent: "center", mt: 2 }}>
        <Pagination
          count={Math.ceil(companies.length / itemsPerPage)}
          page={currentPage}
          onChange={handlePageChange}
          color="primary"
        />
      </Box>
      <Modal open={openModal} onClose={handleCloseModal}>
        <StyledModalContent>
          <Tooltip title="Close Modal">
            <IconButton
              onClick={handleCloseModal}
              style={{ position: "absolute", top: "10px", right: "10px" }}
            >
              <CloseIcon />
            </IconButton>
          </Tooltip>

          <InputLabel style={{ marginTop: "2rem" }}>Company Name</InputLabel>
          <TextField
            fullWidth
            margin="normal"
            value={newCompany.name}
            onChange={(e) =>
              setNewCompany({ ...newCompany, name: e.target.value })
            }
          />
          <InputLabel style={{ marginTop: "1rem" }}>Company Logo</InputLabel>
          <input
            type="file"
            accept="image/*"
            onChange={handleLogoChange}
            style={{ margin: ".5rem auto" }}
          />
          {logoPreview && (
            <img
              src={logoPreview}
              alt="Logo Preview"
              style={{
                maxWidth: "100%",
                maxHeight: "200px",
                marginBottom: "1rem",
              }}
            />
          )}
          <div
            style={{
              display: "flex",
              justifyContent: "flex-end",
              marginTop: "2rem",
            }}
          >
            <Button
              variant="contained"
              color="primary"
              onClick={handleAddNewCompany}
              fullWidth
              sx={{
                background: "linear-gradient(to right, #8000BA, #FF4B00)",
                boxShadow: "0px 3px 6px rgba(0, 0, 0, 0.1)",
                color: "white",
                transition:
                  "transform 0.3s ease-in-out, background 0.3s ease-in-out",
                "&:hover": {
                  background: "linear-gradient(to right, #FF4B00, #8000BA)",
                  transform: "scale(1.05)",
                },
              }}
            >
              Add Company
            </Button>
          </div>
        </StyledModalContent>
      </Modal>
    </Box>
  );
};

export default Companies;
