import React, { useState } from "react";
import {
  Table,
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Paper,
  Button,
  Modal,
  TextField,
  IconButton,
  Box,
  InputLabel,
  styled,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import CloseIcon from "@mui/icons-material/Close";

const StyledModalContent = styled("div")(({ theme }) => ({
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  backgroundColor: "#fff",
  borderRadius: "5px",
  maxWidth: "70%",
  padding: "20px",
  boxShadow: theme.shadows[5],
  outline: "none",
}));

const Companies = () => {
  const [companies, setCompanies] = useState([]);
  const [openModal, setOpenModal] = useState(false);
  const [newCompany, setNewCompany] = useState({
    name: "",
    logo: null,
  });
  const [donatedLengths, setDonatedLengths] = useState({});
  const [logoPreview, setLogoPreview] = useState(null);

  const handleAddCompany = () => {
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
    setNewCompany({
      name: "",
      logo: null,
    });
    setLogoPreview(null);
  };

  const handleAddNewCompany = () => {
    // Add the new company to the list
    setCompanies([...companies, newCompany]);
    // Close the modal
    handleCloseModal();
  };

  const handleAddDonatedLength = (companyId, length) => {
    setDonatedLengths({
      ...donatedLengths,
      [companyId]: (donatedLengths[companyId] || 0) + parseFloat(length),
    });
  };

  const handleLogoChange = (event) => {
    const file = event.target.files[0];
    setNewCompany({
      ...newCompany,
      logo: file,
    });
    // Preview the logo image
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setLogoPreview(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <Box maxWidth="70%" margin="2rem auto" paddingTop="4rem" position="relative">
      <IconButton
        onClick={handleAddCompany}
        style={{ position: "absolute", top: 0, right: 0 }}
      >
        <AddIcon />
      </IconButton>

      <TableContainer component={Paper}>
        {/* ... (Table component code) */}
      </TableContainer>

      <Modal open={openModal} onClose={handleCloseModal}>
        <StyledModalContent>
          <IconButton
            onClick={handleCloseModal}
            style={{ position: "absolute", top: "10px", right: "10px" }}
          >
            <CloseIcon />
          </IconButton>
          <InputLabel>Company Name</InputLabel>
          <TextField
            fullWidth
            margin="normal"
            value={newCompany.name}
            onChange={(e) => setNewCompany({ ...newCompany, name: e.target.value })}
          />
          <InputLabel>Company Logo</InputLabel>
          <input
            type="file"
            accept="image/*"
            onChange={handleLogoChange}
            style={{ marginBottom: "1rem" }}
          />
          {logoPreview && (
            <img
              src={logoPreview}
              alt="Logo Preview"
              style={{ maxWidth: "100%", maxHeight: "200px", marginBottom: "1rem" }}
            />
          )}
          <Button variant="contained" color="primary" onClick={handleAddNewCompany}>
            Add Company
          </Button>
        </StyledModalContent>
      </Modal>
    </Box>
  );
};

export default Companies;
